mapper.select:
SELECT boardNo, crewNo, boardCategoryNo, memberNo, boardTitle, boardContent, boardStatus, createdAt, updatedAt 
FROM (SELECT boardNo, crewNo, boardCategoryNo, memberNo, boardTitle, boardContent, boardStatus, createdAt, updatedAt )
FROM  board
WHERE crewNo = #{crewNo}
ORDER BY  boardNo DESC
LIMIT #{boardEndNo} OFFSET #{boardStartNo};

        SELECT boardNo, crewNo, boardCategoryNo, memberNo, boardTitle, boardContent, boardStatus, createdAt, updatedAt
        FROM (SELECT boardNo, crewNo, boardCategoryNo, memberNo, boardTitle, boardContent, boardStatus, createdAt, updatedAt)
        FROM board
        WHERE boardCategoryNo = #{boardCategoryNo}
        ORDER BY boardNo DESC
		LIMIT #{boardEndNo} OFFSET #{boardStartNo};

SELECT boardNo, crewNo, boardCategoryNo, memberNo, boardTitle, boardContent, boardStatus, createdAt, updatedAt
FROM board
WHERE boardNo = #{boardNo}

mapper.update:
UPDATE board
SET boardCategoryNo = #{boardCategoryNo},
    boardTitle = #{boardTitle},
    boardContent = #{boardContent}
WHERE boardNo = #{boardNo}

mapper.delete:
UPDATE board
SET boardStatus = 0
WHERE boardNo = #{boardNo}

mapper.update:
UPDATE board
SET boardHit = boardHit + 1
WHERE boardNo = #{boardNo}
