mapper.select:
SELECT crewMemberNo, crewNo, memberNo, crewMemberStatus, createdAt 
FROM crew_member 
WHERE crewNo = #{crewNo} AND memberNo = #{memberNo}

mapper.update:
UPDATE crew_member
SET crewMemberStatus = #{crewMemberStatus}
WHERE crewNo = #{crewNo} AND memberNo = #{memberNo}

UPDATE crew
SET crewAttend = crewAttend + #{no}
WHERE crewNo = #{crewNo}

mapper.select:
SELECT count(*)
FROM crew
WHERE categoryBigNo = #{categoryBigNo}

SELECT count(*)
FROM crew_like
WHERE crewNo = #{crewNo} AND memberNo = #{memberNo}
