mapper.select:
SELECT memberNo, geoCode, memberPhone, memberPw, memberNickname, memberIntro, memberStatus, memberOriginalImg, memberSaveImg, memberDeadDate, createdAt, updatedAt
FROM member
WHERE memberPhone = #{memberPhone} AND memberPw = #{memberPw}

SELECT memberNo 
FROM member
WHERE memberPhone = #{memberPhone}

mapper.update:
UPDATE member 
SET memberNickname = #{memberNickname}, geoCode = #{geoCode}, memberIntro = #{memberIntro}, memberOriginalImg = #{memberOriginalImg}, memberSaveImg = #{memberSaveImg}, updatedAt = DEFAULT 
WHERE memberNo = #{memberNo}

UPDATE member 
SET memberPw = #{memberPw} 
WHERE memberPhone = #{memberPhone}

UPDATE member
SET memberStatus = 0 
WHERE memberNo = #{memberNo}

mapper.select:
SELECT memberNo, geoCode, memberPhone, memberPw, memberNickname, memberIntro, memberStatus, memberOriginalImg, memberSaveImg, memberDeadDate, createdAt, updatedAt 
FROM member 
WHERE memberNo = #{memberNo}
